//     token:"",
// });


const AuthProvider =({children})=>{
    const [auth,setAuth] = useState({
        user:null,
        token:"",
    })
    useEffect 
    return (<authContext.Provider value={[auth,setAuth]}>
        {children}
    </authContext.Provider>)
}


//custom hooks

const useAuth=()=>useContext(authContext);

